(window.webpackJsonp=window.webpackJsonp||[]).push([[22],{445:function(t,s,v){"use strict";v.r(s);var n=v(1),p=Object(n.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("p",[t._v("我有一部很喜欢的电影叫《蓝色大门》，里面有一句话：")]),t._v(" "),s("div",{staticClass:"custom-block danger"},[s("p",{staticClass:"title"}),s("p",[t._v("“夏天都快过完了，好像什么都没有做，对啊，好像就只是跑来跑去，什么事都没有做。真的什么事都没有做，但总会留下一些什么吧，你想到什么？留下什么，我们就变成什么样的大人。”")])]),s("p",[t._v("2023，我成为怎样的大人了呢？更成熟了吗？好像没有，更沉稳了吗？好像也没有。一个合格的成年人应该具有的品质，我好像都没有具备。想象在遥远的未来，有人问我：2023，你的十九岁是怎样度过的？我可能只能自豪的回答道：和几位心中惦念不已的朋友留下了些许难忘的回忆。")]),t._v(" "),s("p",[t._v("一份总结应该怎样写呢？以前高中老师老让我们在某些大型考试之后写总结，那时的总结无非也就是瞎编几个“令人信服”的失分原因，装作“痛定思痛”的模样写下一些沮丧的文字。学业、爱情、家庭、友情……都可以成为总结的一部分，排名第一应该是学业。很好，我的学业从绩点上说依旧没什么起色，暑假集训很累很辛苦，学了一些算法但似乎也没达到融会贯通、炉火纯青的程度，拿得出手的是一个水赛国三。第二名应该是家庭，我家一如既往，与往年没差。第三名是友情，其实想把它第二，但家人重要好吧，交了很多新朋友，一些当前阶段很想好好珍惜的人，希望下一个元旦，他们还在我身边。至于爱情为什么最后一名，我喜欢告诫自己没了爱情的人生并不会一无是处以掩盖我依旧母胎单身的事实，没事，我妈说了我们家的人读书的时候就是没人喜欢。婚姻是爱情的坟墓，就像我信服爱情是个人主义自由的坟墓（当然，瞎说。虽然哪怕我再怎么当一个失意的人，我也不会成为伟大小说家，写出什么好话好文章来，海盐，出一个余华就够了。")]),t._v(" "),s("p",[t._v("我像一个精神领袖，精神独裁，审判我周遭的人和事，带有着些许戾气，年轻人需要锋利的刀子般的锐利，我如是相信着。年轻人有年轻人的活法，管他什么中老年。具体表现为什么呢，表现为有时候说话不过脑子，骂人喜欢用隐喻，不讨喜，没办法我没有长一张无时无刻想着讨好别人的嘴。个人逻辑自洽最重要，我笃信我有。当我渐渐意识到自己在某些方面变得不再勇敢，比如不再在课上发表自己的观点，不再忍心在在意的人面前说狠话，变得圆滑？是成为大人的必由之路么，也许吧。这样看来，这路走起来是很残忍的。")]),t._v(" "),s("p",[t._v("也许是悲观主义作祟，我是一个拥有一些浪漫主义想法的现实的悲观主义者，今日的我无法做到彻彻底底的夸赞自己以及对自己的未来抱有一些什么积极的想法，嘴上说着“未来皆坦途”但我实在不相信我有，因为我过去经历了太多。初中班主任说：邓小平都有三起三落呢。可我不是邓小平，我是三落还没起。我同学说：你总是这样悲观。我爸说：我永远相信我女儿不会是一个一事无成的人。难以描述听到这句话的当下内心排山倒海的感动的波澜。不过，悲观不是不快乐，不是不自信，只是不相信幸福会来的如此轻易。")]),t._v(" "),s("p",[t._v("无法预知接下来的一年，我将会面对什么，遇到什么挑战，也无法保证现在陪着我的人，是否能完整陪我走过接下来的一年，但我真的希望可以，就算不可以也没事，暂时的水泥封心应该不是很难（闭嘴吧，我乌鸦嘴就是很灵。嗯，还是陪我走完新的一年吧！一定要在啊，一个都不能少。")]),t._v(" "),s("p",[t._v("二零二四，请多多指教。")])])}),[],!1,null,null,null);s.default=p.exports}}]);